
var utils = require('protean'),
    LinkedList = utils.LinkedList,
    Path;

Path = LinkedList.extend({
    
    get: function (src) {
        var prev;
        
        return this.reduce(function (acc, segment) {
            var ctx;
            
            if (prev) {
                ctx = prev.apply(acc, segment)
            }
            else {
                ctx = segment.get(acc);
            }
            
            prev = segment;
            return ctx;
            
        }, src);
    }
    
});
