var utils = require('protean'),
    Query;
//---------------------------------------------------------------------------
// Query
//---------------------------------------------------------------------------
Query = utils.classify({
    constructor: function Query (rules) {
        this.rules = rules;
    },
    
    test: function (path) {
        return this.rules.some(function (rule) {
            return rule.test(path);
        });
    }
}, {
    toString: { value: function () {
        return this.rules.join(',');
    } }
});
//---------------------------------------------------------------------------
// Exports
//---------------------------------------------------------------------------
module.exports = Query;
