
var utils = require('protean'),
    PStrScan = require('pstrscan');

function Scanner (opts) {
    var aliases;
    
    opts = opts || {};
    
    PStrScan.call(this);

    this.typeKey  = opts.typeKey;
    this.idKey    = opts.idKey;
    
    aliases = opts.aliases;
    
    if (aliases) {
        this.aliases  = aliases;

        this.aliasesRegExp = new RegExp('(' +
            utils.chain(aliases).
                keys().
                map(function (key) {
                    return aliases[key].source;
                }).
                join('|') +
            ')');
    }
}

Scanner.prototype = Object.create(PStrScan.prototype, {
    replace: { value: function (text) {
        var aliases = this.aliases;
        
        if (aliases) {
            return text.replace(this.aliasesRegExp, function (match) {
                return aliases[match];
            });
        }
        
        return text;
    }, enumerable: true }
});
//---------------------------------------------------------------------------
// Exports
//---------------------------------------------------------------------------
module.exports = Scanner;
